import "./App.css";
import Cards from "./components/Cards";
import NavBar from "./components/NavBar";
import Bus from "./components/Bus";
import { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Footer from "./components/Footer";
import Covid from "./components/Covid";

function App() {
	const [busNumber, setBusNumber] = useState(0);
	const [covidCasesDeath, setCovidCasesDeath] = useState(0);
	const [addedCards, setAddedCards] = useState([]);
	const [cards] = useState([
		{
			id: 1,
			name: "Subotica",
			image:
				"https://subotica024.rs/wp-content/uploads/2022/05/subotica024-rs-202205-1176-1536x864.jpg",
			description:
				"Naš najseverniji grad koji predstavlja spoj više nacija, kultura i umetnosti. Subotica se prvi put pominje 1391. pod latinskim imenom Zabatka. Godine 1526,—1527. Subotica je bila prestonica kratkotrajne srpske države samoproglašenog cara Jovana Nenada. Osmansko carstvo je vladalo gradom od 1542. do 1686, kada je Subotica postala posed Habzburške monarhije. Tokom osmanske uprave ime grada je bilo Sobotka. ",
			stringSize: 11,
			inBus: false,
		},
		{
			id: 2,
			name: "Novi Sad",
			image:
				"https://images.musicstore.de/images/1600/gibson-les-paul-tribute-satin-tobacco-sunburst_1_GIT0049503-000.jpg",
			description:
				"The Gibson Les Paul Tribute Satin Tobacco Burst captures the Vibe sound of the legendary Singlecut electric  skillfully and convinces at the same time with agile playing characteristics. For example, the lightweight mahogany body takes a comfortable position on both the belt and the thigh thanks to - Ultra Modern -Weight-Relief, while the rounded profile of the glued maple neck lies comfortably in the hand. In addition, two Gibson 490 pickups with -II Alnico magnets provide the classically powerful Les Paul with endless sustain, which has always been Rock regarded as the perfect foundation for blues. In addition to traditional Vintage Deluxe Keystone mechanics.",
			stringSize: 11,
			inBus: false,
		},
		{
			id: 3,
			name: "Zlatibor",
			image:
				"https://images.musicstore.de/images/1600/jackson-x-series-king-v-kvxmg-gloss-black_1_GIT0050161-000.jpg",
			description:
				"The Jackson X Series King V KVXMG Gloss Black is a premium metal  with high quality features and optimal playing characteristics. Based on the sleek King-V design with elaborate neck-through construction, the electric  features a full-length maple neck with Laurel fingerboard and poplar body wing. The flat Speed Neck profile and the compound radius, which increases from 12 to 16, ensure extremely manoeuvrable handling, which makes every riff and solos much easier. In addition, the proven combination of an EMG 85 on the neck and an EMG 81 on the bridge, which is the gold standard for hot rock and metal sounds for a reason, ensures adequate sound conversion. With their high transparency and crisp attack, the active humbuckers precisely reproduce every note, even in drop tunings. Last but not least, the Jackson X Series King V KVXMG Gloss Black features a Floyd Rose Special Double Locking tremolo for uninhibited lead techniques with squeals and dive bombs.",
			stringSize: 11,
			inBus: false,
		},
	]);

	function refreshFnc() {
		let newCards = cards.filter((g) => g.inBus === true);
		setAddedCards(newCards);
	}

	function setStringSize(id, stringSize) {
		cards
			.filter((card) => card.id === id)
			.forEach((card) => (card.stringSize = stringSize));
	}

	function addCard(card) {
		cards
			.filter((g) => g.id === card.id)
			.filter((g) => g.inBus === false)
			.forEach((g) => {
				setBusNumber(busNumber + 1);
				g.inBus = true;
			});

		refreshFnc();
	}

	function removeCard(card) {
		cards
			.filter((g) => g.id === card.id)
			.forEach((g) => {
				g.inBus = false;
				setBusNumber(busNumber - 1);
			});
		refreshFnc();
	}

	return (
		<BrowserRouter className="App">
			<NavBar busNumber={busNumber} />
			<Routes>
				<Route
					path="/"
					element={
						<>
							<h1>Ovo su sve vaše karte: </h1>
							<Cards
								cards={cards}
								onAdd={addCard}
								onRemove={removeCard}
								onSet={setStringSize}
							/>
						</>
					}
				></Route>
				<Route
					path="/bus"
					element={<Bus cards={addedCards} onRemove={removeCard} />}
				></Route>
			</Routes>
			<Covid
				setCovidCasesDeath={setCovidCasesDeath}
				covidCasesDeath={covidCasesDeath}
			/>
			<Footer />
		</BrowserRouter>
	);
}

export default App;
